ax.set_xticks([1, 2.5])
ax.set_xticklabels(["Q Learning", 'Clinician Policy'], rotation=10)
ax.set_ylim(-22,10)

colors=["forestgreen", "blue"]
 
for patch, color in zip(main['boxes'],colors):
    patch.set_facecolor(color)

